    private void btnEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEntrarActionPerformed
        String login = tfLogin.getText();
        String senha = String.valueOf(tfSenha.getPassword());
        Funcionario funcionario = new Funcionario();
        
        if(funcionario.fazerLoginFuncionario(login, senha)){
        
            JOptionPane.showMessageDialog(null, "Logado com sucesso!", "Entrar - Funcionario", JOptionPane.INFORMATION_MESSAGE);
        
        TelaAcoes tela = new TelaAcoes();
        tela.exportarFuncionario(funcionario);
        tela.setVisible(true);
        dispose();
        }
        else{
            JOptionPane.showMessageDialog(null, "Usuario e/ou senha invalido(s)!", "Entrar - Funcionario", JOptionPane.ERROR_MESSAGE);
        }
        
    }//GEN-LAST:event_btnEntrarActionPerformed
    
 
